// src/components/HeroSection.js
import React from 'react';
import styled from 'styled-components';
import bannerImage from '../assets/kubechain-banner.jpg'; // Adjust the path as necessary

const HeroContainer = styled.div`
  background: url(${bannerImage}) no-repeat center center;
  background-size: cover;
  color: #fff;
  text-align: center;
  height: 90vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const HeroTitle = styled.h1`
  font-size: 2.5rem;
  font-weight: 600; // Assuming you imported a font-weight of 600 for Poppins
`;

