// src/components/HeroSection.js
import React from 'react';
import styled from 'styled-components';
import bannerImage from '../assets/kubechain-banner.png'; // Ensure the path matches your file structure

const HeroContainer = styled.div`
  background: url(${bannerImage}) no-repeat center center;
  background-size: cover;
  color: #000; /* Updated text color to black */
  text-align: center;
  height: 90vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const HeroTitle = styled.h1`
  font-size: 2.5rem;
  font-weight: 600; // Assuming you imported a font-weight of 600 for Poppins
  margin-bottom: 1rem;
  text-shadow: 2px 2px 4px rgba(255,255,255,0.5); /* Updated for better contrast */
  text-transform: uppercase;
`;

const HeroSubtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 2rem;
  text-shadow: 1px 1px 2px rgba(255,255,255,0.7); /* Adding subtle text shadow for readability */
`;

const HeroSection = () => (
  <HeroContainer>
    <HeroTitle>Welcome to KubeChain</HeroTitle>
    <HeroSubtitle>The Future of Decentralized Cloud Computing</HeroSubtitle>
  </HeroContainer>
);

export default HeroSection;
