// src/components/HeroSection.js
import React from 'react';
import styled from 'styled-components';
import bannerImage from '../assets/kubechainpng.png'; // Adjust the path as necessary

const HeroContainer = styled.div`
  background: url(${bannerImage}) no-repeat center center;
  background-size: cover;
  color: #fff;
  text-align: center;
  height: 90vh;
  display: flex;
  flex-direction: column;
  justify-content: center;

  background-color: #000;

`;

const HeroTitle = styled.h1`
  font-size: 2.5rem;
  font-weight: 600; // Assuming you imported a font-weight of 600 for Poppins
    margin-bottom: 1rem;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
    text-transform: uppercase;
    text-color:'black';
    textcolor: 'black';

`;

// Rest of the component remains the same


const HeroSubtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 2rem;
`;

const HeroSection = () => (
  <HeroContainer>
    <HeroTitle>Welcome to KubeChain</HeroTitle>
    <HeroSubtitle>The Future of Decentralized Cloud Computing</HeroSubtitle>
  </HeroContainer>
);

export default HeroSection;
